# Compute a new constant, called e-primorial
# using the following formula:
#   1 + sum({n=0, Inf}, 1/n#)
# where 'n#' is the product of the first n primes.

# Example:
#   1 + 1/2 + 1/(2*3) + 1/(2*3*5) + 1/(2*3*5*7)

use 5.010;
use strict;
use warnings;

use bignum (try => 'GMP');
use ntheory qw(forprimes);

my $s = 0;
my $p = 1;

forprimes {
    $s += 1 / ($p *= $_);
}
1000;

say $s;

__END__
0.
